class Game:
    def __init__(self):
        pygame.init()
        self.screen = pygame.display.set_mode((3050, 1080), pygame.FULLSCREEN|pygame.NOFRAME|pygame.SCALED)
        self.scene = pygame.Surface((5000, 1080))
        pygame.display.set_caption("Deneme")
        self.clock = pygame.time.Clock()

        self.level = Level(level1, self.screen, self.scene, self.clock)

    def run(self):
        while True:
            for event in pygame.event.get():
                if event.type == pygame.QUIT:
                    pygame.quit()
                    sys.exit(0)

            self.screen.fill("black")
            self.scene.fill("black")

            self.level.run()
            
            pygame.display.update()
            self.clock.tick(60)

if __name__ == "__main__":
    game = Game()
    game.run()
